import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{o as h,c as p,e}from"./app.79c56929.js";const a={},u=e('<p>\u4ECA\u5929\u8FD9\u4E00\u8BB2\uFF0C\u6211\u5E76\u4E0D\u6253\u7B97\u5E26\u4F60\u53BB\u89E3\u51B3\u65B0\u7684\u4EC0\u4E48\u95EE\u9898\uFF0C\u800C\u662F\u628A\u6211\u4EEC\u5DF2\u7ECF\u8BB2\u8FC7\u7684\u5185\u5BB9\u518D\u4E32\u4E00\u904D\uFF0C\u5C31\u50CF\u5B66\u751F\u65F6\u4EE3\u6BCF\u4E2A\u5B66\u671F\u5373\u5C06\u7ED3\u675F\u65F6\u7684\u4E00\u6B21\u4E32\u8BB2\uFF0C\u6765 \u201C\u56DE\u5473\u201D\u4E0B OAuth 2.0 \u7684\u6574\u4E2A\u77E5\u8BC6\u4F53\u7CFB\u3002</p><p>\u5F53\u7136\u4E86\uFF0C\u6211\u4E5F\u4F1A\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u4E0E\u4F60\u5206\u4EAB\u6211\u5728\u5B9E\u8DF5 OAuth 2.0 \u7684\u8FC7\u7A0B\u4E2D\uFF0C\u79EF\u7D2F\u7684\u6700\u503C\u5F97\u5206\u4EAB\u7684\u7ECF\u9A8C\u3002</p><p>\u597D\uFF0C\u63A5\u4E0B\u6765\u5C31\u8BA9\u6211\u4EEC\u5148\u4E32\u4E00\u4E32 OAuth 2.0 \u7684\u5DE5\u4F5C\u6D41\u7A0B\u5427\u3002</p><h4 id="oauth-2-0-\u5DE5\u4F5C\u6D41\u7A0B\u4E32\u8BB2" tabindex="-1"><a class="header-anchor" href="#oauth-2-0-\u5DE5\u4F5C\u6D41\u7A0B\u4E32\u8BB2" aria-hidden="true">#</a> OAuth 2.0 \u5DE5\u4F5C\u6D41\u7A0B\u4E32\u8BB2</h4><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/beb02a5baf3654c5025238552cd26a2a.jpg" alt="img" loading="lazy"></p><p>\u6211\u4EEC\u4E00\u76F4\u5728\u8BB2 OAuth 2.0 \u662F\u4E00\u79CD\u6388\u6743\u534F\u8BAE\uFF0C\u8FD9\u79CD\u534F\u8BAE\u53EF\u4EE5\u8BA9\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u4EE3\u8868\u7528\u6237\u53BB\u6267\u884C\u88AB\u5141\u8BB8\u7684\u64CD\u4F5C\u3002\u90A3\u4E48\uFF0C\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u5C31\u9700\u8981\u5411\u7528\u6237\u7D22\u53D6\u6388\u6743\u6765\u83B7\u5F97\u90A3\u4E2A\u4EE4\u724C\u3002</p><p>\u6211\u4EEC\u56DE\u60F3\u4E0B\u7B2C 1 \u8BB2\u62DC\u8BBF\u767E\u5EA6\u738B\u603B\u7684\u4F8B\u5B50\u3002\u53EA\u6709\u62FF\u5230\u524D\u53F0\u5C0F\u59D0\u59D0\u7ED9\u4F60\u7684\u95E8\u7981\u5361\uFF0C\u4F60\u624D\u80FD\u591F\u8FDB\u5165\u767E\u5EA6\u5927\u697C\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u76F8\u5F53\u4E8E\u524D\u53F0\u5C0F\u59D0\u59D0\u7ED9\u4F60\u505A\u4E86\u4E00\u6B21\u6388\u6743\uFF0C\u800C\u8FD9\u4E2A\u6388\u6743\u7684\u51ED\u8BC1\u5C31\u662F\u95E8\u7981\u5361\u3002\u5BF9\u5E94\u5230\u6211\u4EEC\u7684\u7CFB\u7EDF\u4E2D\uFF0C\u95E8\u7981\u5361\u4FBF\u76F8\u5F53\u4E8E\u8BBF\u95EE\u4EE4\u724C\u3002</p><p>\u901A\u8FC7\u201C\u4EE3\u8868\u201D\u201C\u6388\u6743\u201D\u8FD9\u6837\u7684\u5173\u952E\u8BCD\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BA4\u8BC6\u5230\uFF0COAuth 2.0 \u662F\u4E00\u4E2A\u6388\u6743\u534F\u8BAE\uFF0C\u4E5F\u662F\u4E00\u4E2A\u5B89\u5168\u534F\u8BAE\u3002\u90A3\u4E48\uFF0C\u5982\u679C\u6211\u8BF4\u5B83\u4E5F\u662F\u4E00\u79CD\u59D4\u6258\u534F\u8BAE\uFF0C\u4F60\u4E5F\u4E0D\u8981\u5403\u60CA\u3002</p><p>\u8BD5\u60F3\u4E00\u4E0B\uFF0C\u7528\u6237\u5728\u5FAE\u4FE1\u5E73\u53F0\u4E0A\u6709\u4FEE\u6539\u6635\u79F0\u3001\u4FEE\u6539\u5934\u50CF\u3001\u4FEE\u6539\u4E2A\u4EBA\u5174\u8DA3\u7684\u6743\u9650\uFF0C\u5F53\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u8BF7\u6C42\u8BA9\u81EA\u5DF1\u4EE3\u8868\u7528\u6237\u6765\u64CD\u4F5C\u8FD9\u4E9B\u6743\u9650\u7684\u65F6\u5019\uFF0C\u5C31\u662F\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u8BF7\u6C42\u7528\u6237\u628A\u8FD9\u4E9B\u6743\u9650\u59D4\u6258\u7ED9\u81EA\u5DF1\uFF0C\u7528\u6237\u5728\u6279\u51C6\u4E86\u59D4\u6258\u8BF7\u6C42\u4E4B\u540E\uFF0C\u624D\u53EF\u4EE5\u4EE3\u8868\u7528\u6237\u53BB\u6267\u884C\u8FD9\u4E9B\u64CD\u4F5C\u3002</p><p>\u8FD9\u65F6\uFF0C\u6211\u4EEC\u7EC6\u60F3\u4E00\u4E0B\uFF0C\u59D4\u6258\u624D\u662F OAuth 2.0 \u6388\u6743\u6982\u5FF5\u7684\u6839\u57FA\uFF0C\u56E0\u4E3A\u6CA1\u6709\u201C\u59D4\u6258\u201D\u4E4B\u610F\u5C31\u4E0D\u4F1A\u6709\u201C\u4EE3\u8868\u201D\u884C\u4E3A\u7684\u53D1\u751F\u3002</p><p>\u5728\u6574\u4E2A\u8BFE\u7A0B\u8BB2\u8FF0\u6388\u6743\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u9891\u7E41\u4E3E\u4F8B\u548C\u5F3A\u8C03\u7684\u5C31\u662F\u6388\u6743\u7801\u8BB8\u53EF\u6D41\u7A0B\u3002\u5728\u5B66\u4E60\u6388\u6743\u7801\u6D41\u7A0B\u7684\u65F6\u5019\uFF0C\u4F60\u6700\u56F0\u60D1\u7684\u4E00\u70B9\u6050\u6015\u83AB\u8FC7\u4E8E \u201C\u4E3A\u4EC0\u4E48\u8981\u591A\u6B64\u4E00\u4E3E\uFF0C\u975E\u5F97\u901A\u8FC7\u4E00\u4E2A\u6388\u6743\u7801 code \u6765\u6362\u53D6\u8BBF\u95EE\u4EE4\u724C access_token\u201D\u4E86\u5427\u3002\u8FD9\u4E2A\u95EE\u9898\u6211\u5728\u8BB2\u6388\u6743\u7801\u8BB8\u53EF\u7684\u6574\u4F53\u6D41\u7A0B\u65F6\u4E5F\u505A\u8FC7\u5206\u6790\u4E86\uFF0C\u4F60\u73B0\u5728\u56DE\u60F3\u8D77\u6765\u5E94\u8BE5\u4E0D\u4F1A\u518D\u4E3A\u6B64\u201C\u75DB\u82E6\u4E0D\u582A\u201D \u4E86\u5427\u3002</p><p>\u6211\u4EEC\u518D\u6765\u5206\u6790\u4E0B\uFF0C\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u8981\u83B7\u53D6\u8BBF\u95EE\u4EE4\u724C\uFF0C\u53EA\u80FD\u901A\u8FC7\u4E24\u4E2A\u6E20\u9053\uFF1A</p><ul><li>\u4E00\u4E2A\u6E20\u9053\u662F\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u524D\u7AEF\u9875\u9762\u3002\u4F46\u662F\uFF0C\u5982\u679C\u76F4\u63A5\u8FD4\u56DE\u5230\u524D\u7AEF\u9875\u9762\u4E0A\uFF0C\u8BBF\u95EE\u4EE4\u724C\u662F\u5F88\u5BB9\u6613\u88AB\u901A\u8FC7\u6D4F\u89C8\u5668\u622A\u83B7\u7684\uFF0C\u6240\u4EE5\u663E\u7136\u4E0D\u53EF\u53D6\u3002</li><li>\u53E6\u5916\u4E00\u4E2A\u6E20\u9053\u662F\u901A\u8FC7\u540E\u7AEF\u4F20\u8F93\u3002\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u540E\u7AEF\u548C\u6388\u6743\u670D\u52A1\u7684\u540E\u7AEF\u4E4B\u95F4\u901A\u4FE1\uFF0C\u8FD9\u6837\u5C31\u53EF\u4EE5\u907F\u514D\u4EE4\u724C\u88AB\u76F4\u63A5\u66B4\u9732\u7684\u95EE\u9898\u3002</li></ul><p>\u518D\u5F80\u6DF1\u4E86\u60F3\uFF0C\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u540E\u7AEF\u603B\u4E0D\u80FD\u5411\u6388\u6743\u670D\u52A1\u7684\u540E\u7AEF \u201C\u786C\u8981\u201D \u5427\uFF0C\u603B\u8981\u544A\u8BC9\u6388\u6743\u670D\u52A1\u662F\u8981\u54EA\u4E2A\u7528\u6237\u7684 access_token \u5427\uFF0C\u6240\u4EE5\u8FD8\u9700\u8981\u7528\u6237\u7684\u53C2\u4E0E\u3002</p><p>\u7528\u6237\u4E00\u65E6\u53C2\u4E0E\u8FDB\u6765\uFF0C\u8BBF\u95EE\u7684\u7B2C\u4E00\u4E2A\u9875\u9762\u662F\u7B2C\u4E09\u65B9\u8F6F\u4EF6\uFF0C\u7528\u6237\u8981\u6388\u6743\uFF0C\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u5C31\u9700\u8981\u628A\u7528\u6237\u5F15\u5BFC\u5230\u6388\u6743\u670D\u52A1\u9875\u9762\u3002\u4F46\u8FD9\u4E2A\u65F6\u5019\uFF0C\u7528\u6237\u5C31\u8DDF\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u4E4B\u95F4\u6CA1\u6709\u4EFB\u4F55\u201C\u901A\u4FE1\u8FDE\u63A5\u201D\u4E86\u3002\u5982\u679C\u6388\u6743\u670D\u52A1\u901A\u8FC7\u540E\u7AEF\u901A\u4FE1\u76F4\u63A5\u5C06\u4EE4\u724C\u7ED9\u4E86\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u540E\u7AEF\uFF0C\u90A3\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u8BE5\u5982\u4F55\u901A\u77E5\u7528\u6237\u5462\uFF0C\u6050\u6015\u5C31\u4E0D\u592A\u597D\u5B9E\u73B0\u4E86\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u4E0B\u5C31\u5F88\u5DE7\u5999\u5730\u5F15\u5165\u4E86\u6388\u6743\u7801 code\uFF1A\u5148\u628A code \u901A\u8FC7\u91CD\u5B9A\u5411\u8FD4\u56DE\u5230\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u9875\u9762\uFF1B\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u901A\u8FC7\u6D4F\u89C8\u5668\u83B7\u53D6\u5230 code \u540E\uFF0C\u518D\u901A\u8FC7\u540E\u7AEF\u901A\u4FE1\u6362\u53D6 access_token\uFF1B\u5F85\u62FF\u5230 token \u4E4B\u540E\uFF0C\u7531\u4E8E\u6B64\u65F6\u7528\u6237\u5DF2\u7ECF\u5728\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u670D\u52A1\u4E0A\uFF0C\u6240\u4EE5\u53EF\u4EE5\u5F88\u5BB9\u6613\u5730\u901A\u77E5\u5230\u7528\u6237\u3002</p><p>\u4EE5\u4E0A\uFF0C\u5C31\u662F\u6388\u6743\u7801\u8BB8\u53EF\u7684\u6574\u4F53\u5DE5\u4F5C\u6D41\u7A0B\u4E86\u3002\u6211\u4EEC\u8BF4\uFF0C\u8FD9\u662F OAuth 2.0 \u6388\u6743\u4F53\u7CFB\u4E2D\u6700\u5B8C\u5907\u7684\u6D41\u7A0B\uFF0C\u5176\u4ED6\u7684\u6388\u6743\u8BB8\u53EF\u7C7B\u578B\uFF0C\u6BD4\u5982\u8D44\u6E90\u62E5\u6709\u8005\u51ED\u636E\u8BB8\u53EF\u3001\u5BA2\u6237\u7AEF\u51ED\u636E\u8BB8\u53EF\u3001\u9690\u5F0F\u8BB8\u53EF\uFF0C\u90FD\u662F\u4EE5\u6B64\u4E3A\u57FA\u7840\u3002\u56E0\u6B64\uFF0C\u53EA\u8981\u4F60\u80FD\u7406\u89E3\u6388\u6743\u7801\u8BB8\u53EF\u7684\u6D41\u7A0B\uFF0C\u4E5F\u5C31\u638C\u63E1\u4E86\u6574\u4E2A OAuth 2.0 \u4E2D\u6240\u6709\u8BB8\u53EF\u7C7B\u578B\u7684\u8FD0\u8F6C\u673A\u5236\uFF0C\u5728\u5B9E\u9645\u5DE5\u4F5C\u573A\u666F\u4E2D\u7528\u4E0A OAuth 2.0 \u5C06\u4E0D\u518D\u662F\u95EE\u9898\u3002</p><h3 id="oauth-2-0-\u5B89\u5168\u95EE\u9898\u4E32\u8BB2" tabindex="-1"><a class="header-anchor" href="#oauth-2-0-\u5B89\u5168\u95EE\u9898\u4E32\u8BB2" aria-hidden="true">#</a> OAuth 2.0 \u5B89\u5168\u95EE\u9898\u4E32\u8BB2</h3><p>\u4F46\u662F\uFF0C\u5230\u8FD9\u91CC\u5E76\u6CA1\u6709\u4E07\u4E8B\u5927\u5409\uFF0C\u6211\u4EEC\u53EA\u662F\u89E3\u51B3\u4E86 OAuth 2.0 \u7684\u57FA\u7840\u4F7F\u7528\u7684\u95EE\u9898\u3002\u8981\u60F3\u7528\u597D\u3001\u7528\u5BF9\u8FD9\u4E2A\u534F\u8BAE\uFF0C\u6210\u957F\u4E3A\u8FD9\u4E2A\u534F\u8BAE\u7684\u5E94\u7528\u4E13\u5BB6\uFF0C\u6211\u4EEC\u8FD8\u5FC5\u987B\u5173\u6CE8 OAu</p><p>\u6211\u4EEC\u5728\u5B9E\u8DF5 OAuth 2.0 \u7684\u8FC7\u7A0B\u4E2D\uFF0C\u8FD8\u5FC5\u987B\u6309\u7167\u89C4\u8303\u5EFA\u8BAE\u6765\u6267\u884C\uFF0C\u5426\u5219\u4FBF\u4F1A\u5F15\u53D1\u4E00\u7CFB\u5217\u7684\u5B89\u5168\u95EE\u9898\u3002\u8FD9\u4E5F\u5F80\u5F80\u5BFC\u81F4\u6709\u7684\u540C\u5B66\u4F1A\u53D1\u51FA\u8FD9\u6837\u7684\u7591\u95EE\uFF0COAuth 2.0 \u4E0D\u662F\u5B89\u5168\u7684\u5417\uFF1F\u5B83\u4E0D\u662F\u4E00\u76F4\u5728\u4FDD\u62A4\u7740\u4E92\u8054\u7F51\u4E0A\u6210\u5343\u4E0A\u4E07\u4E2A Web API \u5417\uFF0C\u6211\u4EEC\u4E0D\u4E5F\u8BF4\u5B83\u662F\u4E00\u79CD\u5B89\u5168\u534F\u8BAE\u5417\uFF1F</p><p>\u9996\u5148\u6211\u4EEC\u8BF4 OAuth 2.0 \u662F\u5B89\u5168\u534F\u8BAE\u6CA1\u95EE\u9898\uFF0C\u4F46\u5982\u679C\u4F7F\u7528\u4E0D\u5F53\u4E5F\u4F1A\u5F15\u8D77\u5B89\u5168\u4E0A\u7684\u95EE\u9898\u3002\u6BD4\u5982\uFF0C\u6211\u4EEC\u5728\u7B2C 8 \u8BB2\u4E2D\u63D0\u5230\u4E86\u4E00\u4E2A\u5F88\u5E7F\u6CDB\u7684\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u95EE\u9898\u3002\u4E4B\u6240\u4EE5\u51FA\u73B0\u8FD9\u6837\u7684\u5B89\u5168\u95EE\u9898\uFF0C\u5C31\u662F\u56E0\u4E3A\u6211\u4EEC\u6CA1\u6709\u9075\u5FAA OAuth 2.0 \u7684\u4F7F\u7528\u5EFA\u8BAE\uFF0C\u6BD4\u5982\u6CA1\u6709\u4F7F\u7528 state \u8FD9\u6837\u7684\u53C2\u6570\u6765\u505A\u8BF7\u6C42\u7684\u6821\u9A8C\uFF0C\u6216\u8005\u662F\u6CA1\u6709\u9075\u5FAA\u6388\u6743\u7801 code \u503C\u53EA\u80FD\u4F7F\u7528\u4E00\u6B21\uFF0C\u5E76\u4E14\u8FD8\u8981\u6E05\u9664\u4F7F\u7528\u8FC7\u7684 code \u503C\u8DDF token \u503C\u4E4B\u95F4\u7684\u7ED1\u5B9A\u5173\u7CFB\u7684\u5EFA\u8BAE\u3002</p><p>\u5728\u5B89\u5168\u95EE\u9898\u4E0A\uFF0C\u5176\u5B9E\u6211\u4EEC\u4E00\u76F4\u90FD\u6CA1\u6709\u7279\u522B\u8BF4\u660E\u4E00\u70B9\uFF0C\u90A3\u5C31\u662F\u5728\u4F7F\u7528 OAuth 2.0 \u7684\u6D41\u7A0B\u4E2D\uFF0C\u6211\u4EEC\u7684 HTTP \u901A\u4FE1\u8981\u4F7F\u7528 HTTPS \u534F\u8BAE\u6765\u4FDD\u62A4\u6570\u636E\u4F20\u8F93\u7684\u5B89\u5168\u6027\u3002\u8FD9\u662F\u56E0\u4E3A OAuth 2.0 \u652F\u6301\u7684 bearer \u4EE4\u724C\u7C7B\u578B\uFF0C\u4E5F\u5C31\u662F\u4EFB\u610F\u5B57\u7B26\u4E32\u683C\u5F0F\u7684\u4EE4\u724C\uFF0C\u5E76\u6CA1\u6709\u63D0\u4F9B\u4E14\u6CA1\u6709\u8981\u6C42\u4F7F\u7528\u4FE1\u606F\u7B7E\u540D\u7684\u673A\u5236\u3002</p><p>\u4F60\u53EF\u80FD\u4F1A\u8BF4\uFF0CJWT \u4EE4\u724C\u6709\u8FD9\u6837\u7684\u52A0\u5BC6\u673A\u5236\u554A\u3002\u4F46\u5176\u5B9E\uFF0C\u8FD9\u4E5F\u6B63\u8BF4\u660E\u4E86 OAuth 2.0 \u662F\u4E00\u4E2A\u6CA1\u6709\u7EA6\u675F\u666E\u901A\u4EE4\u724C\u7684\u89C4\u5219\uFF0C\u6240\u4EE5\u624D\u6709\u4E86 JWT \u8FD9\u6837\u5BF9 OAuth 2.0 \u7684\u989D\u5916\u8865\u5145\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0CJWT \u8DDF OAuth 2.0 \u5E76\u6CA1\u6709\u76F4\u63A5\u5173\u7CFB\uFF0C\u5B83\u53EA\u662F\u4E00\u79CD\u7ED3\u6784\u5316\u7684\u4FE1\u606F\u5B58\u50A8\uFF0C\u53EF\u4EE5\u88AB\u7528\u5728\u9664\u4E86 OAuth 2.0 \u4EE5\u5916\u7684\u4EFB\u4F55\u5730\u65B9\u3002\u6BD4\u5982\uFF0C\u91CD\u7F6E\u5BC6\u7801\u7684\u65F6\u5019\uFF0C\u4F1A\u7ED9\u4F60\u7684\u90AE\u7BB1\u53D1\u9001\u4E00\u4E2A\u94FE\u63A5\uFF0C\u8FD9\u4E2A\u94FE\u63A5\u5C31\u9700\u8981\u80FD\u591F\u6807\u8BC6\u51FA\u7528\u6237\u662F\u8C01\u3001\u4E0D\u80FD\u7BE1\u6539\u3001\u6709\u6548\u671F 5 \u5206\u949F\uFF0C\u8FD9\u4E9B\u7279\u5F81\u90FD\u8DDF JWT \u76F8\u7B26\u5408\u3002\u4E5F\u5C31\u662F\u8BF4\uFF0CJWT \u5E76\u4E0D\u662F OAuth 2.0 \u534F\u8BAE\u89C4\u8303\u6240\u6DB5\u76D6\u7684\u5185\u5BB9\u3002</p><p>OAuth 2.0 \u4F3C\u4E4E\u6CA1\u6709\u81EA\u5DF1\u7684\u89C4\u5219\u7EA6\u675F\u673A\u5236\uFF0C\u6216\u8005\u8BF4\u53EA\u6709\u6BD4\u8F83\u5F31\u7684\u7EA6\u675F\uFF0C\u4F46\u5176\u5B9E\u4E0D\u662F\u4E0D\u7EA6\u675F\uFF0C\u800C\u662F\u5B83\u5C31\u81F4\u529B\u4E8E\u505A\u597D\u6388\u6743\u6846\u67B6\u8FD9\u4E00\u4EF6\u4E8B\u513F\u3002\u901A\u8FC7\u6211\u4EEC\u524D\u9762\u7684\u5B66\u4E60\uFF0C\u4E5F\u53EF\u4EE5\u9A8C\u8BC1\u51FA\u5B83\u7684\u786E\u5F88\u597D\u5730\u80DC\u4EFB\u4E86\u8FD9\u9879\u5DE5\u4F5C\u3002</p><p>\u4E5F\u8BB8\u6B63\u662F\u56E0\u4E3A OAuth 2.0 \u53EF\u4EE5\u652F\u6301\u7C7B\u4F3C OIDC \u8FD9\u6837\u7684\u8EAB\u4EFD\u8BA4\u8BC1\u534F\u8BAE\uFF0C\u5BFC\u81F4\u6211\u4EEC\u603B\u662F\u201C\u575A\u6301\u201D\u8BA4\u4E3A OAuth 2.0 \u662F\u4E00\u79CD\u8EAB\u4EFD\u8BA4\u8BC1\u534F\u8BAE\u3002\u5F53\u7136\u4E86\uFF0COAuth 2.0 \u5E76\u4E0D\u662F\u8EAB\u4EFD\u8BA4\u8BC1\u534F\u8BAE\uFF0C\u6211\u5728\u7B2C 9 \u8BB2\u4E2D\u7528\u201C\u9762\u7C89\u201D\u548C\u201C\u9762\u5305\u201D\u6765\u7C7B\u6BD4 OAuth 2.0 \u548C OIDC \u7684\u5173\u7CFB\u3002</p><p>\u8FD9\u91CC\u6211\u518D\u89E3\u91CA\u4E00\u4E0B\u3002\u7A76\u7ADF\u662F\u4EC0\u4E48\u539F\u56E0\u5BFC\u81F4\u4E86\u6211\u4EEC\u5BF9 OAuth 2.0 \u6709\u8FD9\u6837\u7684 \u201C\u8BEF\u89E3\u201D \u5462\uFF1F\u6211\u60F3\u5927\u6982\u539F\u56E0\u662F\uFF0COAuth 2.0 \u4E2D\u786E\u5B9E\u5305\u542B\u4E86\u8EAB\u4EFD\u8BA4\u8BC1\u7684\u5185\u5BB9\uFF0C\u5373\u6388\u6743\u670D\u52A1\u9700\u8981\u8BA9\u7528\u6237\u767B\u5F55\u4EE5\u540E\u624D\u53EF\u4EE5\u8FDB\u884C\u7528\u6237\u786E\u8BA4\u6388\u6743\u7684\u64CD\u4F5C\u3002</p><p>\u4F46\u8FD9\u6837\u7684\u6D41\u7A0B\uFF0C\u4EC5\u4EC5\u662F OAuth 2.0 \u6D89\u53CA\u5230\u4E86\u8EAB\u4EFD\u8BA4\u8BC1\u7684\u884C\u4E3A\uFF0C\u8FD8\u4E0D\u8DB3\u4EE5\u8BA9 OAuth 2.0 \u6210\u4E3A\u4E00\u4E2A\u771F\u6B63\u7684\u7528\u6237\u8EAB\u4EFD\u8BA4\u8BC1\u534F\u8BAE\u3002\u56E0\u4E3A OAuth 2.0 \u5173\u5FC3\u7684\u53EA\u6709\u4E24\u70B9\uFF0C\u9881\u53D1\u4EE4\u724C\u548C\u4F7F\u7528\u4EE4\u724C\uFF0C\u5E76\u4E14\u4EE4\u724C\u5BF9\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u662F\u4E0D\u900F\u660E\u7684\uFF1B\u540C\u65F6\uFF0C\u53D7\u4FDD\u62A4\u8D44\u6E90\u670D\u52A1\u4E5F\u4E0D\u5173\u5FC3\u662F\u54EA\u4E2A\u7528\u6237\u6765\u8BF7\u6C42\uFF0C\u53EA\u8981\u6709\u5408\u6CD5\u7684\u4EE4\u724C \u201C\u9012\u201D \u8FC7\u6765\uFF0C\u5C31\u4F1A\u7ED9\u51FA\u6B63\u786E\u7684\u54CD\u5E94\uFF0C\u628A\u6570\u636E\u8FD4\u56DE\u7ED9\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u3002</p><p>\u4EE5\u4E0A\uFF0C\u5C31\u662F\u4E0E OAuth 2.0 \u5B89\u5168\u95EE\u9898\u606F\u606F\u76F8\u5173\u7684\u5185\u5BB9\u4E86\u3002\u8BB2\u5230\u8FD9\u91CC\uFF0C\u5E0C\u671B\u4F60\u53EF\u4EE5\u6309\u7167\u81EA\u5DF1\u7684\u7406\u89E3\uFF0C\u878D\u4F1A\u8D2F\u901A OAuth 2.0 \u7684\u8FD9\u4E9B\u6838\u5FC3\u77E5\u8BC6\u4E86\u3002\u63A5\u4E0B\u6765\uFF0C\u6211\u518D\u548C\u4F60\u5206\u4EAB\u4E00\u4E2A\u6211\u5728\u5B9E\u8DF5 OAuth 2.0 \u8FC7\u7A0B\u4E2D\u611F\u89E6\u6700\u6DF1\u7684\u4E00\u4E2A\u95EE\u9898\u5427\u3002</p><h3 id="\u518D\u5F3A\u8C03\u90FD\u4E0D\u4E3A\u8FC7\u7684\u5B89\u5168\u610F\u8BC6" tabindex="-1"><a class="header-anchor" href="#\u518D\u5F3A\u8C03\u90FD\u4E0D\u4E3A\u8FC7\u7684\u5B89\u5168\u610F\u8BC6" aria-hidden="true">#</a> \u518D\u5F3A\u8C03\u90FD\u4E0D\u4E3A\u8FC7\u7684\u5B89\u5168\u610F\u8BC6</h3><p>\u6839\u636E\u6211\u5728\u5F00\u653E\u5E73\u53F0\u4E0A\u8FD9\u4E9B\u5E74\u7684\u5DE5\u4F5C\u7ECF\u9A8C\uFF0C\u5B89\u5168\u610F\u8BC6\u662F\u5B9E\u8DF5 OAuth 2.0 \u8FC7\u7A0B\u4E2D\uFF0C\u518D\u600E\u4E48\u5F3A\u8C03\u90FD\u4E0D\u4E3A\u8FC7\u7684\u95EE\u9898\u3002</p><p>\u56E0\u4E3A\u603B\u7ED3\u8D77\u6765\uFF0C\u8981\u8BF4\u4F7F\u7528 OAuth 2.0 \u7684\u8FC7\u7A0B\u4E2D\u5982\u679C\u80FD\u6709\u54EA\u4E2A\u673A\u4F1A\u8BA9\u4F60\u201C\u683D\u4E2A\u5927\u8DDF\u5934\u201D\u7684\u8BDD\uFF0C\u90A3\u8FD9\u4E2A\u673A\u4F1A\u4E00\u5B9A\u662F\u5728\u5B89\u5168\u4E0A\uFF1AOAuth 2.0 \u672C\u5C31\u662F\u81F4\u529B\u4E8E\u4FDD\u62A4\u5F00\u653E\u7684 Web API\uFF0C\u4FDD\u62A4\u7528\u6237\u5728\u5E73\u53F0\u4E0A\u7684\u8D44\u6E90\uFF0C\u5982\u679C\u56E0\u4E3A OAuth 2.0 \u4F7F\u7528\u4E0D\u5F53\u800C\u9020\u6210\u5B89\u5168\u95EE\u9898\uFF0C\u786E\u5B9E\u662F\u4E00\u4EF6\u975E\u5E38 \u201C\u4E22\u4EBA\u201D \u7684\u4E8B\u60C5\u3002</p><p>\u800C OAuth2.0 \u7684\u6D41\u7A0B\u91CC\u9762\u80FD\u591F\u4E3A\u5B89\u5168\u505A\u8D21\u732E\u7684\u53EA\u6709\u4E24\u65B9\uFF0C\u4E00\u65B9\u662F\u7B2C\u4E09\u65B9\u8F6F\u4EF6\uFF0C\u4E00\u65B9\u662F\u5E73\u53F0\u65B9\u3002\u5728\u5B89\u5168\u6027\u8FD9\u4E2A\u95EE\u9898\u4E0A\uFF0C\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u5F00\u53D1\u8005\u7684\u5B89\u5168\u610F\u8BC6\u53C2\u5DEE\u4E0D\u9F50\u3002\u90A3\u9488\u5BF9\u8FD9\u4E00\u70B9\uFF0C\u5C31\u9700\u8981\u5E73\u53F0\u65B9\u5728\u5176\u5B98\u65B9\u6587\u6863\u4E0A\u91CD\u7B14\u63CF\u8FF0\uFF0C\u5E76\u7ED9\u51FA\u5E38\u89C1\u5B89\u5168\u6F0F\u6D1E\u76F8\u5E94\u7684\u89E3\u51B3\u65B9\u6848\u3002\u540C\u65F6\uFF0C\u4F5C\u4E3A\u5E73\u53F0\u65B9\u7684\u5185\u90E8\u5F00\u53D1\u4EBA\u5458\uFF0C\u5BF9\u5B89\u5168\u7684\u95EE\u9898\u540C\u6837\u4E0D\u80FD\u5FFD\u89C6\uFF0C\u800C\u4E14\u8981\u6709\u66F4\u9AD8\u7684\u5B89\u5168\u610F\u8BC6\u548C\u8BA4\u77E5\u3002</p><p>\u53EA\u6709\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u5F00\u53D1\u8005\u548C\u5E73\u53F0\u65B9\u7684\u7814\u53D1\u4EBA\u5458\u5171\u540C\u4FDD\u6709\u8F83\u9AD8\u7684\u5B89\u5168\u610F\u8BC6\uFF0C\u624D\u80FD\u8BA9\u201C\u5B89\u5168\u7684\u5899\u201D\u5792\u5F97\u8D8A\u6765\u8D8A\u9AD8\uFF0C\u8BA9\u653B\u51FB\u8005\u7684\u6210\u672C\u8D8A\u6765\u8D8A\u9AD8\u3002\u56E0\u4E3A\u5B89\u5168\u7684\u672C\u8D28\u5C31\u662F\u6210\u672C\u95EE\u9898\u3002</p><p>\u4F60\u770B\uFF0C\u6211\u82B1\u4E86\u8FD9\u4E48\u5927\u7684\u7BC7\u5E45\u6765\u548C\u4F60\u8BB2\u89E3 OAuth 2.0 \u7684\u5B89\u5168\u95EE\u9898\uFF0C\u5E76\u5355\u72EC\u5206\u6790\u4E86\u5B89\u5168\u610F\u8BC6\uFF0C\u662F\u4E0D\u662F\u8DB3\u4EE5\u51F8\u663E\u5B89\u5168\u6027\u8FD9\u4E2A\u95EE\u9898\u7684\u91CD\u8981\u7A0B\u5EA6\u4E86\u3002\u6CA1\u9519\u513F\uFF0C\u8FD9\u4E5F\u662F\u4F60\u80FD\u7528\u597D OAuth 2.0 \u7684\u4E00\u4E2A\u5173\u952E\u6807\u5FD7\u3002</p><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><p>\u597D\u4E86\uFF0C\u4EE5\u4E0A\u5C31\u662F\u6211\u4EEC\u4ECA\u5929\u7684\u4E3B\u8981\u5185\u5BB9\u4E86\u3002\u6211\u5E0C\u671B\u4F60\u80FD\u8BB0\u4F4F\u4EE5\u4E0B\u4E09\u70B9\uFF1A</p><ol><li><p>OAuth 2.0 \u662F\u4E00\u4E2A\u6388\u6743\u534F\u8BAE\uFF0C\u5B83\u901A\u8FC7\u8BBF\u95EE\u4EE4\u724C\u6765\u8868\u793A\u8FD9\u79CD\u6388\u6743\u3002\u7B2C\u4E09\u8F6F\u4EF6\u62FF\u5230\u8BBF\u95EE\u4EE4\u724C\u4E4B\u540E\uFF0C\u5C31\u53EF\u4EE5\u4F7F\u7528\u8BBF\u95EE\u4EE4\u724C\u6765\u4EE3\u8868\u7528\u6237\u53BB\u8BBF\u95EE\u7528\u6237\u7684\u6570\u636E\u4E86\u3002\u6240\u4EE5\uFF0C\u6211\u4EEC\u8BF4\u6388\u6743\u7684\u6838\u5FC3\u5C31\u662F\u83B7\u53D6\u8BBF\u95EE\u4EE4\u724C\u548C\u4F7F\u7528\u8BBF\u95EE\u4EE4\u724C\u3002</p></li><li><p>OAuth 2.0 \u662F\u4E00\u4E2A\u5B89\u5168\u534F\u8BAE\uFF0C\u4F46\u662F\u5982\u679C\u4F60\u4F7F\u7528\u4E0D\u5F53\uFF0C\u5B83\u5E76\u4E0D\u80FD\u4FDD\u8BC1\u4E00\u5B9A\u662F\u5B89\u5168\u7684\u3002\u5982\u679C\u4F60\u4E0D\u6309\u7167 OAuth 2.0 \u89C4\u8303\u4E2D\u7684\u5EFA\u8BAE\u6765\u5B9E\u65BD\uFF0C\u5C31\u4F1A\u6709\u5B89\u5168\u98CE\u9669\u3002\u6BD4\u5982\uFF0C\u4F60\u6CA1\u6709\u9075\u5FAA\u6388\u6743\u670D\u52A1\u4E2D\u7684\u6388\u6743\u7801\u53EA\u80FD\u4F7F\u7528\u4E00\u6B21\u3001\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u91CD\u5B9A\u5411 URL \u8981\u7CBE\u786E\u5339\u914D\u7B49\u5EFA\u8BAE\u3002</p></li><li><p>\u5B89\u5168\u9632\u62A4\u7684\u8FC7\u7A0B\u4E00\u76F4\u90FD\u662F\u201C\u9B54\u9AD8\u4E00\u5C3A\u9053\u9AD8\u4E00\u4E08\u201D\uFF0C\u76F8\u4E92\u6500\u5347\u7684\u8FC7\u7A0B\u3002\u56E0\u6B64\uFF0C\u5728\u4F7F\u7528 OAuth 2.0 \u7684\u8FC7\u7A0B\u4E2D\uFF0C\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u548C\u5E73\u53F0\u65B9\u90FD\u8981\u6709\u8DB3\u591F\u7684\u5B89\u5168\u610F\u8BC6\uFF0C\u6765\u628A\u201C\u5B89\u5168\u7684\u5899\u201D\u7B51\u5F97\u66F4\u9AD8\u3002</p><p>\u6700\u540E\u6211\u60F3\u8BF4\u7684\u662F\uFF0C\u65E0\u8BBA\u4F60\u4F7F\u7528 OAuth 2.0 \u76EE\u7684\u662F\u4FDD\u62A4 API\uFF0C\u8FD8\u662F\u4F5C\u4E3A\u7528\u6237\u8EAB\u4EFD\u8BA4\u8BC1\u7684\u57FA\u7840\uFF0COAuth 2.0 \u90FD\u53EA\u662F\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\u7684\u4E00\u79CD\u5DE5\u5177\u3002\u800C\u638C\u63E1 OAuth 2.0 \u8FD9\u79CD\u5DE5\u5177\u7684\u539F\u7406\u53CA\u5176\u4F7F\u7528\u573A\u666F\uFF0C\u5C06\u4F1A\u5E2E\u52A9\u4F60\u66F4\u9AD8\u6548\u3001\u66F4\u4F18\u96C5\u5730\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\u3002</p></li></ol><h3 id="\u601D\u8003\u9898" tabindex="-1"><a class="header-anchor" href="#\u601D\u8003\u9898" aria-hidden="true">#</a> \u601D\u8003\u9898</h3><p>\u5982\u679C\u4F60\u662F\u4E00\u540D\u7B2C\u4E09\u65B9\u8F6F\u4EF6\u7684\u5F00\u53D1\u4EBA\u5458\uFF0C\u4F60\u89C9\u5F97\u5E94\u8BE5\u5982\u4F55\u63D0\u9AD8\u81EA\u5DF1\u7684\u5B89\u5168\u610F\u8BC6\u5462\uFF1F</p>',40),A=[u];function O(c,i){return h(),p("div",null,A)}var d=t(a,[["render",O],["__file","10.html.vue"]]);export{d as default};
