import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";import{o as t,c as n,b as p,a as o,d as e,e as r,r as s}from"./app.ffe4bde8.js";const i={},l=e("\u672C\u6587\u662F\u8F6C\u8F7D\u4E8E"),d={href:"https://zhuanlan.zhihu.com/p/115912936",target:"_blank",rel:"noopener noreferrer"},h=e("\u77E5\u4E4E100%\u5F04\u660E\u767D5\u79CDIO\u6A21\u578B"),g=e("\uFF0C\u539F\u4F5C\u8005"),f={href:"https://www.zhihu.com/people/duan-pan-ykjym",target:"_blank",rel:"noopener noreferrer"},I=e("\u52E4\u52B3\u7684\u5C0F\u624B"),O=e("\uFF0C\u8F6C\u8F7D\u5230\u6211\u4E2A\u4EBA\u535A\u5BA2\u4EC5\u4EC5\u662F\u5907\u4EFD\uFF0C\u56E0\u4E3A\u6B64\u535A\u5BA2\u5199\u7684\u771F\u7684\u592A\u68D2\u4E86\uFF0C\u9632\u6B62\u4E22\u5931\uFF0C\u5982\u6709\u4FB5\u6743\uFF0C\u8BF7\u8054\u7CFB\u6211\u5220\u9664\u3002"),_=r('<h2 id="_1\u3001\u4ECEtcp\u53D1\u9001\u6570\u636E\u7684\u6D41\u7A0B\u8BF4\u8D77" tabindex="-1"><a class="header-anchor" href="#_1\u3001\u4ECEtcp\u53D1\u9001\u6570\u636E\u7684\u6D41\u7A0B\u8BF4\u8D77" aria-hidden="true">#</a> 1\u3001\u4ECETCP\u53D1\u9001\u6570\u636E\u7684\u6D41\u7A0B\u8BF4\u8D77</h2><p>\u8981\u6DF1\u5165\u7684\u7406\u89E3\u5404\u79CDIO\u6A21\u578B\uFF0C\u90A3\u4E48\u5FC5\u987B\u5148\u4E86\u89E3\u4E0B\u4EA7\u751F\u5404\u79CDIO\u7684\u539F\u56E0\u662F\u4EC0\u4E48\uFF0C\u8981\u77E5\u9053\u8FD9\u5176\u4E2D\u7684\u672C\u8D28\u95EE\u9898\u90A3\u4E48\u6211\u4EEC\u5C31\u5FC5\u987B\u8981\u77E5\u9053\u4E00\u6761\u6D88\u606F\u662F\u5982\u4F55\u4ECE\u4E00\u4E2A\u4EBA\u53D1\u9001\u5230\u53E6\u5916\u4E00\u4E2A\u4EBA\u7684\uFF1B</p><p>\u4EE5\u4E24\u4E2A\u5E94\u7528\u7A0B\u5E8F\u901A\u8BAF\u4E3A\u4F8B\uFF0C\u6211\u4EEC\u6765\u4E86\u89E3\u4E00\u4E0B\u5F53\u201CA\u201D\u5411&quot;B&quot; \u53D1\u9001\u4E00\u6761\u6D88\u606F\uFF0C\u7B80\u5355\u6765\u8BF4\u4F1A\u7ECF\u8FC7\u5982\u4E0B\u6D41\u7A0B\uFF1A</p><p>\u7B2C\u4E00\u6B65\uFF1A\u5E94\u7528A\u628A\u6D88\u606F\u53D1\u9001\u5230 TCP\u53D1\u9001\u7F13\u51B2\u533A\u3002</p><p>\u7B2C\u4E8C\u6B65\uFF1A TCP\u53D1\u9001\u7F13\u51B2\u533A\u518D\u628A\u6D88\u606F\u53D1\u9001\u51FA\u53BB\uFF0C\u7ECF\u8FC7\u7F51\u7EDC\u4F20\u9012\u540E\uFF0C\u6D88\u606F\u4F1A\u53D1\u9001\u5230B\u670D\u52A1\u5668\u7684TCP\u63A5\u6536\u7F13\u51B2\u533A\u3002</p><p>\u7B2C\u4E09\u6B65\uFF1AB\u518D\u4ECETCP\u63A5\u6536\u7F13\u51B2\u533A\u53BB\u8BFB\u53D6\u5C5E\u4E8E\u81EA\u5DF1\u7684\u6570\u636E\u3002</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100621.png" alt="20221227100621" loading="lazy"></p><p>\u6839\u636E\u4E0A\u56FE\u6211\u4EEC\u57FA\u672C\u4E0A\u4E86\u89E3\u6D88\u606F\u53D1\u9001\u8981\u7ECF\u8FC7 \u5E94\u7528A\u3001\u5E94\u7528A\u5BF9\u5E94\u670D\u52A1\u5668\u7684TCP\u53D1\u9001\u7F13\u51B2\u533A\u3001\u7ECF\u8FC7\u7F51\u7EDC\u4F20\u8F93\u540E\u6D88\u606F\u53D1\u9001\u5230\u4E86\u5E94\u7528B\u5BF9\u5E94\u670D\u52A1\u5668TCP\u63A5\u6536\u7F13\u51B2\u533A\u3001\u7136\u540E\u6700\u7EC8B\u5E94\u7528\u8BFB\u53D6\u5230\u6D88\u606F\u3002</p><p>\u5982\u679C\u7406\u89E3\u4E86\u4E0A\u9762\u7684\u6D88\u606F\u53D1\u9001\u6D41\u7A0B\uFF0C\u90A3\u4E48\u6211\u4EEC\u4E0B\u9762\u5F00\u59CB\u8FDB\u5165\u6587\u7AE0\u7684\u4E3B\u9898\uFF1B</p><h2 id="_2\u3001\u963B\u585Eio-\u975E\u963B\u585Eio" tabindex="-1"><a class="header-anchor" href="#_2\u3001\u963B\u585Eio-\u975E\u963B\u585Eio" aria-hidden="true">#</a> 2\u3001\u963B\u585EIO |\u975E\u963B\u585EIO</h2><p>\u4EEC\u628A\u89C6\u89D2\u5207\u6362\u5230\u4E0A\u9762\u56FE\u4E2D\u7684\u7B2C\u4E09\u6B65\uFF0C \u4E5F\u5C31\u662F\u5E94\u7528B\u4ECETCP\u7F13\u51B2\u533A\u4E2D\u8BFB\u53D6\u6570\u636E\u3002</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100706.png" alt="20221227100706" loading="lazy"></p><p>\u601D\u8003\u4E00\u4E2A\u95EE\u9898\uFF1A</p><p>\u56E0\u4E3A\u5E94\u7528\u4E4B\u95F4\u53D1\u9001\u6D88\u606F\u662F\u95F4\u65AD\u6027\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4\u5728\u4E0A\u56FE\u4E2DTCP\u7F13\u51B2\u533A\u8FD8\u6CA1\u6709\u63A5\u6536\u5230\u5C5E\u4E8E\u5E94\u7528B\u8BE5\u8BFB\u53D6\u7684\u6D88\u606F\u65F6\uFF0C\u90A3\u4E48\u6B64\u65F6\u5E94\u7528B\u5411TCP\u7F13\u51B2\u533A\u53D1\u8D77\u8BFB\u53D6\u7533\u8BF7\uFF0CTCP\u63A5\u6536\u7F13\u51B2\u533A\u662F\u5E94\u8BE5\u9A6C\u4E0A\u544A\u8BC9\u5E94\u7528B \u73B0\u5728\u6CA1\u6709\u4F60\u7684\u6570\u636E\uFF0C\u8FD8\u662F\u8BF4\u8BA9\u5E94\u7528B\u5728\u8FD9\u91CC\u7B49\u7740\uFF0C\u76F4\u5230\u6709\u6570\u636E\u518D\u628A\u6570\u636E\u4EA4\u7ED9\u5E94\u7528B\u3002</p><p>\u628A\u8FD9\u4E2A\u95EE\u9898\u5E94\u7528\u5230\u7B2C\u4E00\u4E2A\u6B65\u9AA4\u4E5F\u662F\u4E00\u6837\uFF0C\u5E94\u7528A\u5728\u5411TCP\u53D1\u9001\u7F13\u51B2\u533A\u53D1\u9001\u6570\u636E\u65F6\uFF0C\u5982\u679CTCP\u53D1\u9001\u7F13\u51B2\u533A\u5DF2\u7ECF\u6EE1\u4E86\uFF0C\u90A3\u4E48\u662F\u544A\u8BC9\u5E94\u7528A\u73B0\u5728\u6CA1\u7A7A\u95F4\u4E86\uFF0C\u8FD8\u662F\u8BA9\u5E94\u7528A\u7B49\u5F85\u7740\uFF0C\u7B49TCP\u53D1\u9001\u7F13\u51B2\u533A\u6709\u7A7A\u95F4\u4E86\u518D\u628A\u5E94\u7528A\u7684\u6570\u636E\u8BBF\u62F7\u8D1D\u5230\u53D1\u9001\u7F13\u51B2\u533A\u3002</p><h3 id="_2-1-\u4EC0\u4E48\u662F\u963B\u585Eio" tabindex="-1"><a class="header-anchor" href="#_2-1-\u4EC0\u4E48\u662F\u963B\u585Eio" aria-hidden="true">#</a> 2.1 \u4EC0\u4E48\u662F\u963B\u585EIO</h3><p>\u5982\u679C\u4E0A\u9762\u7684\u95EE\u9898\u4F60\u5DF2\u7ECF\u601D\u8003\u8FC7\u4E86\uFF0C\u90A3\u4E48\u5176\u5B9E\u4F60\u5DF2\u7ECF\u660E\u767D\u4E86\u4EC0\u4E48\u662F\u963B\u585EIO\u4E86\uFF0C\u6240\u8C13\u963B\u585EIO\u5C31\u662F\u5F53\u5E94\u7528B\u53D1\u8D77\u8BFB\u53D6\u6570\u636E\u7533\u8BF7\u65F6\uFF0C\u5728\u5185\u6838\u6570\u636E\u6CA1\u6709\u51C6\u5907\u597D\u4E4B\u524D\uFF0C\u5E94\u7528B\u4F1A\u4E00\u76F4\u5904\u4E8E\u7B49\u5F85\u6570\u636E\u72B6\u6001\uFF0C\u76F4\u5230\u5185\u6838\u628A\u6570\u636E\u51C6\u5907\u597D\u4E86\u4EA4\u7ED9\u5E94\u7528B\u624D\u7ED3\u675F\u3002</p><p>\u672F\u8BED\u63CF\u8FF0\uFF1A\u5728\u5E94\u7528\u8C03\u7528recvfrom\u8BFB\u53D6\u6570\u636E\u65F6\uFF0C\u5176\u7CFB\u7EDF\u8C03\u7528\u76F4\u5230\u6570\u636E\u5305\u5230\u8FBE\u4E14\u88AB\u590D\u5236\u5230\u5E94\u7528\u7F13\u51B2\u533A\u4E2D\u6216\u8005\u53D1\u9001\u9519\u8BEF\u65F6\u624D\u8FD4\u56DE\uFF0C\u5728\u6B64\u671F\u95F4\u4E00\u76F4\u4F1A\u7B49\u5F85\uFF0C\u8FDB\u7A0B\u4ECE\u8C03\u7528\u5230\u8FD4\u56DE\u8FD9\u6BB5\u65F6\u95F4\u5185\u90FD\u662F\u88AB\u963B\u585E\u7684\u79F0\u4E3A\u963B\u585EIO\uFF1B</p><p>\u6D41\u7A0B\uFF1A</p><p>1\u3001\u5E94\u7528\u8FDB\u7A0B\u5411\u5185\u6838\u53D1\u8D77recfrom\u8BFB\u53D6\u6570\u636E\u3002</p><p>2\u3001\u51C6\u5907\u6570\u636E\u62A5\uFF08\u5E94\u7528\u8FDB\u7A0B\u963B\u585E\uFF09\u3002</p><p>3\u3001\u5C06\u6570\u636E\u4ECE\u5185\u6838\u8D1F\u8D23\u5230\u5E94\u7528\u7A7A\u95F4\u3002</p><p>4\u3001\u590D\u5236\u5B8C\u6210\u540E\uFF0C\u8FD4\u56DE\u6210\u529F\u63D0\u793A\u3002</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100729.png" alt="20221227100729" loading="lazy"></p><h3 id="_2-2-\u4EC0\u4E48\u662F\u975E\u963B\u585Eio" tabindex="-1"><a class="header-anchor" href="#_2-2-\u4EC0\u4E48\u662F\u975E\u963B\u585Eio" aria-hidden="true">#</a> 2.2 \u4EC0\u4E48\u662F\u975E\u963B\u585EIO</h3><p>\u6211\u6562\u4FDD\u8BC1\u5982\u679C\u4F60\u5DF2\u7ECF\u7406\u89E3\u4E86\u963B\u585EIO\uFF0C\u90A3\u4E48\u5FC5\u5B9A\u5DF2\u7ECF\u77E5\u9053\u4E86\u4EC0\u4E48\u662F\u975E\u963B\u585EIO\u3002\u6309\u7167\u4E0A\u9762\u7684\u601D\u8DEF\uFF0C\u6240\u8C13\u975E\u963B\u585EIO\u5C31\u662F\u5F53\u5E94\u7528B\u53D1\u8D77\u8BFB\u53D6\u6570\u636E\u7533\u8BF7\u65F6\uFF0C\u5982\u679C\u5185\u6838\u6570\u636E\u6CA1\u6709\u51C6\u5907\u597D\u4F1A\u5373\u523B\u544A\u8BC9\u5E94\u7528B\uFF0C\u4E0D\u4F1A\u8BA9B\u5728\u8FD9\u91CC\u7B49\u5F85\u3002</p><p>\u672F\u8BED\uFF1A\u975E\u963B\u585EIO\u662F\u5728\u5E94\u7528\u8C03\u7528recvfrom\u8BFB\u53D6\u6570\u636E\u65F6\uFF0C\u5982\u679C\u8BE5\u7F13\u51B2\u533A\u6CA1\u6709\u6570\u636E\u7684\u8BDD\uFF0C\u5C31\u4F1A\u76F4\u63A5\u8FD4\u56DE\u4E00\u4E2AEWOULDBLOCK\u9519\u8BEF\uFF0C\u4E0D\u4F1A\u8BA9\u5E94\u7528\u4E00\u76F4\u7B49\u5F85\u4E2D\u3002\u5728\u6CA1\u6709\u6570\u636E\u7684\u65F6\u5019\u4F1A\u5373\u523B\u8FD4\u56DE\u9519\u8BEF\u6807\u8BC6\uFF0C\u90A3\u4E5F\u610F\u5473\u7740\u5982\u679C\u5E94\u7528\u8981\u8BFB\u53D6\u6570\u636E\u5C31\u9700\u8981\u4E0D\u65AD\u7684\u8C03\u7528recvfrom\u8BF7\u6C42\uFF0C\u76F4\u5230\u8BFB\u53D6\u5230\u5B83\u6570\u636E\u8981\u7684\u6570\u636E\u4E3A\u6B62\u3002</p><p>\u6D41\u7A0B\uFF1A</p><p>1\u3001\u5E94\u7528\u8FDB\u7A0B\u5411\u5185\u6838\u53D1\u8D77recvfrom\u8BFB\u53D6\u6570\u636E\u3002</p><p>2\u3001\u6CA1\u6709\u6570\u636E\u62A5\u51C6\u5907\u597D\uFF0C\u5373\u523B\u8FD4\u56DEEWOULDBLOCK\u9519\u8BEF\u7801\u3002</p><p>3\u3001\u5E94\u7528\u8FDB\u7A0B\u5411\u5185\u6838\u53D1\u8D77recvfrom\u8BFB\u53D6\u6570\u636E\u3002</p><p>4\u3001\u5DF2\u6709\u6570\u636E\u5305\u51C6\u5907\u597D\u5C31\u8FDB\u884C\u4E00\u4E0B \u6B65\u9AA4\uFF0C\u5426\u5219\u8FD8\u662F\u8FD4\u56DE\u9519\u8BEF\u7801\u3002</p><p>5\u3001\u5C06\u6570\u636E\u4ECE\u5185\u6838\u62F7\u8D1D\u5230\u7528\u6237\u7A7A\u95F4\u3002</p><p>6\u3001\u5B8C\u6210\u540E\uFF0C\u8FD4\u56DE\u6210\u529F\u63D0\u793A\u3002</p><h2 id="_3\u3001io\u590D\u7528\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#_3\u3001io\u590D\u7528\u6A21\u578B" aria-hidden="true">#</a> 3\u3001IO\u590D\u7528\u6A21\u578B</h2><p>\u5982\u679C\u4F60\u5DF2\u7ECF\u660E\u767D\u4E86\u975E\u963B\u585EIO\u7684\u5DE5\u4F5C\u6A21\u5F0F\uFF0C\u90A3\u4E48\u63A5\u4E0B\u6765\u6211\u4EEC\u7EE7\u7EED\u4E86\u89E3IO\u590D\u7528\u6A21\u578B\u7684\u4EA7\u751F\u539F\u56E0\u548C\u601D\u8DEF\u3002</p><p>\u601D\u8003\u4E00\u4E2A\u95EE\u9898\uFF1A</p><p>\u6211\u4EEC\u8FD8\u662F\u628A\u89C6\u89D2\u653E\u5230\u5E94\u7528B\u4ECETCP\u7F13\u51B2\u533A\u4E2D\u8BFB\u53D6\u6570\u636E\u8FD9\u4E2A\u73AF\u8282\u6765\u3002\u5982\u679C\u5728\u5E76\u53D1\u7684\u73AF\u5883\u4E0B\uFF0C\u53EF\u80FD\u4F1AN\u4E2A\u4EBA\u5411\u5E94\u7528B\u53D1\u9001\u6D88\u606F\uFF0C\u8FD9\u79CD\u60C5\u51B5\u4E0B\u6211\u4EEC\u7684\u5E94\u7528\u5C31\u5FC5\u987B\u521B\u5EFA\u591A\u4E2A\u7EBF\u7A0B\u53BB\u8BFB\u53D6\u6570\u636E\uFF0C\u6BCF\u4E2A\u7EBF\u7A0B\u90FD\u4F1A\u81EA\u5DF1\u8C03\u7528recvfrom \u53BB\u8BFB\u53D6\u6570\u636E\u3002\u90A3\u4E48\u6B64\u65F6\u60C5\u51B5\u53EF\u80FD\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100808.png" alt="20221227100808" loading="lazy"></p><p>\u5982\u4E0A\u56FE\u4E00\u6837\uFF0C\u5E76\u53D1\u60C5\u51B5\u4E0B\u670D\u52A1\u5668\u5F88\u53EF\u80FD\u4E00\u77AC\u95F4\u4F1A\u6536\u5230\u51E0\u5341\u4E0A\u767E\u4E07\u7684\u8BF7\u6C42\uFF0C\u8FD9\u79CD\u60C5\u51B5\u4E0B\u5E94\u7528B\u5C31\u9700\u8981\u521B\u5EFA\u51E0\u5341\u4E0A\u767E\u4E07\u7684\u7EBF\u7A0B\u53BB\u8BFB\u53D6\u6570\u636E\uFF0C\u540C\u65F6\u53C8\u56E0\u4E3A\u5E94\u7528\u7EBF\u7A0B\u662F\u4E0D\u77E5\u9053\u4EC0\u4E48\u65F6\u5019\u4F1A\u6709\u6570\u636E\u8BFB\u53D6\uFF0C\u4E3A\u4E86\u4FDD\u8BC1\u6D88\u606F\u80FD\u53CA\u65F6\u8BFB\u53D6\u5230\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u7EBF\u7A0B\u81EA\u5DF1\u5FC5\u987B\u4E0D\u65AD\u7684\u5411\u5185\u6838\u53D1\u9001recvfrom \u8BF7\u6C42\u6765\u8BFB\u53D6\u6570\u636E\uFF1B</p><p>\u90A3\u4E48\u95EE\u9898\u6765\u4E86\uFF0C\u8FD9\u4E48\u591A\u7684\u7EBF\u7A0B\u4E0D\u65AD\u8C03\u7528recvfrom \u8BF7\u6C42\u6570\u636E\uFF0C\u5148\u4E0D\u8BF4\u670D\u52A1\u5668\u80FD\u4E0D\u80FD\u625B\u5F97\u4F4F\u8FD9\u4E48\u591A\u7EBF\u7A0B\uFF0C\u5C31\u7B97\u625B\u5F97\u4F4F\u90A3\u4E48\u5F88\u660E\u663E\u8FD9\u79CD\u65B9\u5F0F\u662F\u4E0D\u662F\u592A\u6D6A\u8D39\u8D44\u6E90\u4E86\uFF0C\u7EBF\u7A0B\u662F\u6211\u4EEC\u64CD\u4F5C\u7CFB\u7EDF\u7684\u5B9D\u8D35\u8D44\u6E90\uFF0C\u5927\u91CF\u7684\u7EBF\u7A0B\u7528\u6765\u53BB\u8BFB\u53D6\u6570\u636E\u4E86\uFF0C\u90A3\u4E48\u5C31\u610F\u5473\u7740\u80FD\u505A\u5176\u5B83\u4E8B\u60C5\u7684\u7EBF\u7A0B\u5C31\u4F1A\u5C11\u3002</p><p>\u6240\u4EE5\uFF0C\u6709\u4EBA\u5C31\u63D0\u51FA\u4E86\u4E00\u4E2A\u601D\u8DEF\uFF0C\u80FD\u4E0D\u80FD\u63D0\u4F9B\u4E00\u79CD\u65B9\u5F0F\uFF0C\u53EF\u4EE5\u7531\u4E00\u4E2A\u7EBF\u7A0B\u76D1\u63A7\u591A\u4E2A\u7F51\u7EDC\u8BF7\u6C42\uFF08\u6211\u4EEC\u540E\u9762\u5C06\u79F0\u4E3Afd\u6587\u4EF6\u63CF\u8FF0\u7B26\uFF0Clinux\u7CFB\u7EDF\u628A\u6240\u6709\u7F51\u7EDC\u8BF7\u6C42\u4EE5\u4E00\u4E2Afd\u6765\u6807\u8BC6\uFF09\uFF0C\u8FD9\u6837\u5C31\u53EF\u4EE5\u53EA\u9700\u8981\u4E00\u4E2A\u6216\u51E0\u4E2A\u7EBF\u7A0B\u5C31\u53EF\u4EE5\u5B8C\u6210\u6570\u636E\u72B6\u6001\u8BE2\u95EE\u7684\u64CD\u4F5C\uFF0C\u5F53\u6709\u6570\u636E\u51C6\u5907\u5C31\u7EEA\u4E4B\u540E\u518D\u5206\u914D\u5BF9\u5E94\u7684\u7EBF\u7A0B\u53BB\u8BFB\u53D6\u6570\u636E\uFF0C\u8FD9\u4E48\u505A\u5C31\u53EF\u4EE5\u8282\u7701\u51FA\u5927\u91CF\u7684\u7EBF\u7A0B\u8D44\u6E90\u51FA\u6765\uFF0C\u8FD9\u4E2A\u5C31\u662FIO\u590D\u7528\u6A21\u578B\u7684\u601D\u8DEF\u3002</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100820.png" alt="20221227100820" loading="lazy"></p><p>\u6B63\u5982\u4E0A\u56FE\uFF0CIO\u590D\u7528\u6A21\u578B\u7684\u601D\u8DEF\u5C31\u662F\u7CFB\u7EDF\u63D0\u4F9B\u4E86\u4E00\u79CD\u51FD\u6570\u53EF\u4EE5\u540C\u65F6\u76D1\u63A7\u591A\u4E2Afd\u7684\u64CD\u4F5C\uFF0C\u8FD9\u4E2A\u51FD\u6570\u5C31\u662F\u6211\u4EEC\u5E38\u8BF4\u5230\u7684select\u3001poll\u3001epoll\u51FD\u6570\uFF0C\u6709\u4E86\u8FD9\u4E2A\u51FD\u6570\u540E\uFF0C\u5E94\u7528\u7EBF\u7A0B\u901A\u8FC7\u8C03\u7528select\u51FD\u6570\u5C31\u53EF\u4EE5\u540C\u65F6\u76D1\u63A7\u591A\u4E2Afd\uFF0Cselect\u51FD\u6570\u76D1\u63A7\u7684fd\u4E2D\u53EA\u8981\u6709\u4EFB\u4F55\u4E00\u4E2A\u6570\u636E\u72B6\u6001\u51C6\u5907\u5C31\u7EEA\u4E86\uFF0Cselect\u51FD\u6570\u5C31\u4F1A\u8FD4\u56DE\u53EF\u8BFB\u72B6\u6001\uFF0C\u8FD9\u65F6\u8BE2\u95EE\u7EBF\u7A0B\u518D\u53BB\u901A\u77E5\u5904\u7406\u6570\u636E\u7684\u7EBF\u7A0B\uFF0C\u5BF9\u5E94\u7EBF\u7A0B\u6B64\u65F6\u518D\u53D1\u8D77recvfrom\u8BF7\u6C42\u53BB\u8BFB\u53D6\u6570\u636E\u3002</p><p>\u672F\u8BED\u63CF\u8FF0\uFF1A\u8FDB\u7A0B\u901A\u8FC7\u5C06\u4E00\u4E2A\u6216\u591A\u4E2Afd\u4F20\u9012\u7ED9select\uFF0C\u963B\u585E\u5728select\u64CD\u4F5C\u4E0A\uFF0Cselect\u5E2E\u6211\u4EEC\u4FA6\u6D4B\u591A\u4E2Afd\u662F\u5426\u51C6\u5907\u5C31\u7EEA\uFF0C\u5F53\u6709fd\u51C6\u5907\u5C31\u7EEA\u65F6\uFF0Cselect\u8FD4\u56DE\u6570\u636E\u53EF\u8BFB\u72B6\u6001\uFF0C\u5E94\u7528\u7A0B\u5E8F\u518D\u8C03\u7528recvfrom\u8BFB\u53D6\u6570\u636E\u3002</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100834.png" alt="20221227100834" loading="lazy"></p><p>\u603B\u7ED3\uFF1A\u590D\u7528IO\u7684\u57FA\u672C\u601D\u8DEF\u5C31\u662F\u901A\u8FC7slect\u6216poll\u3001epoll \u6765\u76D1\u63A7\u591Afd \uFF0C\u6765\u8FBE\u5230\u4E0D\u5FC5\u4E3A\u6BCF\u4E2Afd\u521B\u5EFA\u4E00\u4E2A\u5BF9\u5E94\u7684\u76D1\u63A7\u7EBF\u7A0B\uFF0C\u4ECE\u800C\u51CF\u5C11\u7EBF\u7A0B\u8D44\u6E90\u521B\u5EFA\u7684\u76EE\u7684\u3002</p><h2 id="_4\u3001\u4FE1\u53F7\u9A71\u52A8io\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#_4\u3001\u4FE1\u53F7\u9A71\u52A8io\u6A21\u578B" aria-hidden="true">#</a> 4\u3001\u4FE1\u53F7\u9A71\u52A8IO\u6A21\u578B</h2><p>\u590D\u7528IO\u6A21\u578B\u89E3\u51B3\u4E86\u4E00\u4E2A\u7EBF\u7A0B\u53EF\u4EE5\u76D1\u63A7\u591A\u4E2Afd\u7684\u95EE\u9898\uFF0C\u4F46\u662Fselect\u662F\u91C7\u7528\u8F6E\u8BE2\u7684\u65B9\u5F0F\u6765\u76D1\u63A7\u591A\u4E2Afd\u7684\uFF0C\u901A\u8FC7\u4E0D\u65AD\u7684\u8F6E\u8BE2fd\u7684\u53EF\u8BFB\u72B6\u6001\u6765\u77E5\u9053\u662F\u5426\u6709\u53EF\u8BFB\u7684\u6570\u636E\uFF0C\u800C\u65E0\u8111\u7684\u8F6E\u8BE2\u5C31\u663E\u5F97\u6709\u70B9\u66B4\u529B\uFF0C\u56E0\u4E3A\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\u7684\u8F6E\u8BE2\u90FD\u662F\u65E0\u6548\u7684\uFF0C\u6240\u4EE5\u6709\u4EBA\u5C31\u60F3\uFF0C\u80FD\u4E0D\u80FD\u4E0D\u8981\u6211\u603B\u662F\u53BB\u95EE\u4F60\u662F\u5426\u6570\u636E\u51C6\u5907\u5C31\u7EEA\uFF0C\u80FD\u4E0D\u80FD\u6211\u53D1\u51FA\u8BF7\u6C42\u540E\u7B49\u4F60\u6570\u636E\u51C6\u5907\u597D\u4E86\u5C31\u901A\u77E5\u6211\uFF0C\u6240\u4EE5\u5C31\u884D\u751F\u4E86\u4FE1\u53F7\u9A71\u52A8IO\u6A21\u578B\u3002</p><p>\u4E8E\u662F\u4FE1\u53F7\u9A71\u52A8IO\u4E0D\u662F\u7528\u5FAA\u73AF\u8BF7\u6C42\u8BE2\u95EE\u7684\u65B9\u5F0F\u53BB\u76D1\u63A7\u6570\u636E\u5C31\u7EEA\u72B6\u6001\uFF0C\u800C\u662F\u5728\u8C03\u7528sigaction\u65F6\u5019\u5EFA\u7ACB\u4E00\u4E2ASIGIO\u7684\u4FE1\u53F7\u8054\u7CFB\uFF0C\u5F53\u5185\u6838\u6570\u636E\u51C6\u5907\u597D\u4E4B\u540E\u518D\u901A\u8FC7SIGIO\u4FE1\u53F7\u901A\u77E5\u7EBF\u7A0B\u6570\u636E\u51C6\u5907\u597D\u540E\u7684\u53EF\u8BFB\u72B6\u6001\uFF0C\u5F53\u7EBF\u7A0B\u6536\u5230\u53EF\u8BFB\u72B6\u6001\u7684\u4FE1\u53F7\u540E\uFF0C\u6B64\u65F6\u518D\u5411\u5185\u6838\u53D1\u8D77recvfrom\u8BFB\u53D6\u6570\u636E\u7684\u8BF7\u6C42\uFF0C\u56E0\u4E3A\u4FE1\u53F7\u9A71\u52A8IO\u7684\u6A21\u578B\u4E0B\u5E94\u7528\u7EBF\u7A0B\u5728\u53D1\u51FA\u4FE1\u53F7\u76D1\u63A7\u540E\u5373\u53EF\u8FD4\u56DE\uFF0C\u4E0D\u4F1A\u963B\u585E\uFF0C\u6240\u4EE5\u8FD9\u6837\u7684\u65B9\u5F0F\u4E0B\uFF0C\u4E00\u4E2A\u5E94\u7528\u7EBF\u7A0B\u4E5F\u53EF\u4EE5\u540C\u65F6\u76D1\u63A7\u591A\u4E2Afd\u3002</p><p>\u7C7B\u4F3C\u4E8E\u4E0B\u56FE\u63CF\u8FF0\uFF1A <img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100856.png" alt="20221227100856" loading="lazy"></p><p>\u672F\u8BED\u63CF\u8FF0\uFF1A\u9996\u5148\u5F00\u542F\u5957\u63A5\u53E3\u4FE1\u53F7\u9A71\u52A8IO\u529F\u80FD\uFF0C\u5E76\u901A\u8FC7\u7CFB\u7EDF\u8C03\u7528sigaction\u6267\u884C\u4E00\u4E2A\u4FE1\u53F7\u5904\u7406\u51FD\u6570\uFF0C\u6B64\u65F6\u8BF7\u6C42\u5373\u523B\u8FD4\u56DE\uFF0C\u5F53\u6570\u636E\u51C6\u5907\u5C31\u7EEA\u65F6\uFF0C\u5C31\u751F\u6210\u5BF9\u5E94\u8FDB\u7A0B\u7684SIGIO\u4FE1\u53F7\uFF0C\u901A\u8FC7\u4FE1\u53F7\u56DE\u8C03\u901A\u77E5\u5E94\u7528\u7EBF\u7A0B\u8C03\u7528recvfrom\u6765\u8BFB\u53D6\u6570\u636E\u3002</p><p><img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100909.png" alt="20221227100909" loading="lazy"></p><p>\u603B\u7ED3\uFF1A IO\u590D\u7528\u6A21\u578B\u91CC\u9762\u7684select\u867D\u7136\u53EF\u4EE5\u76D1\u63A7\u591A\u4E2Afd\u4E86\uFF0C\u4F46select\u5176\u5B9E\u73B0\u7684\u672C\u8D28\u4E0A\u8FD8\u662F\u901A\u8FC7\u4E0D\u65AD\u7684\u8F6E\u8BE2fd\u6765\u76D1\u63A7\u6570\u636E\u72B6\u6001\uFF0C \u56E0\u4E3A\u5927\u90E8\u5206\u8F6E\u8BE2\u8BF7\u6C42\u5176\u5B9E\u90FD\u662F\u65E0\u6548\u7684\uFF0C\u6240\u4EE5\u4FE1\u53F7\u9A71\u52A8IO\u610F\u5728\u901A\u8FC7\u8FD9\u79CD\u5EFA\u7ACB\u4FE1\u53F7\u5173\u8054\u7684\u65B9\u5F0F\uFF0C\u5B9E\u73B0\u4E86\u53D1\u51FA\u8BF7\u6C42\u540E\u53EA\u9700\u8981\u7B49\u5F85\u6570\u636E\u5C31\u7EEA\u7684\u901A\u77E5\u5373\u53EF\uFF0C\u8FD9\u6837\u5C31\u53EF\u4EE5\u907F\u514D\u5927\u91CF\u65E0\u6548\u7684\u6570\u636E\u72B6\u6001\u8F6E\u8BE2\u64CD\u4F5C\u3002</p><h2 id="_5\u3001\u5F02\u6B65io" tabindex="-1"><a class="header-anchor" href="#_5\u3001\u5F02\u6B65io" aria-hidden="true">#</a> 5\u3001\u5F02\u6B65IO</h2><p>\u5176\u5B9E\u7ECF\u8FC7\u4E86\u4E0A\u9762\u4E24\u4E2A\u6A21\u578B\u7684\u4F18\u5316\uFF0C\u6211\u4EEC\u7684\u6548\u7387\u6709\u4E86\u5F88\u5927\u7684\u63D0\u5347\uFF0C\u4F46\u662F\u6211\u4EEC\u5F53\u7136\u4E0D\u4F1A\u5C31\u8FD9\u6837\u6EE1\u8DB3\u4E86\uFF0C\u6709\u6CA1\u6709\u66F4\u597D\u7684\u529E\u6CD5\uFF0C\u901A\u8FC7\u89C2\u5BDF\u6211\u4EEC\u53D1\u73B0\uFF0C\u4E0D\u7BA1\u662FIO\u590D\u7528\u8FD8\u662F\u4FE1\u53F7\u9A71\u52A8\uFF0C\u6211\u4EEC\u8981\u8BFB\u53D6\u4E00\u4E2A\u6570\u636E\u603B\u662F\u8981\u53D1\u8D77\u4E24\u9636\u6BB5\u7684\u8BF7\u6C42\uFF0C\u7B2C\u4E00\u6B21\u53D1\u9001select\u8BF7\u6C42\uFF0C\u8BE2\u95EE\u6570\u636E\u72B6\u6001\u662F\u5426\u51C6\u5907\u597D\uFF0C\u7B2C\u4E8C\u6B21\u53D1\u9001recevform\u8BF7\u6C42\u8BFB\u53D6\u6570\u636E\u3002</p><p>\u601D\u8003\u4E00\u4E2A\u95EE\u9898\uFF1A</p><p>\u4E5F\u8BB8\u4F60\u4E00\u5F00\u59CB\u5C31\u6709\u4E00\u4E2A\u7591\u95EE\uFF0C\u4E3A\u4EC0\u4E48\u6211\u4EEC\u660E\u660E\u662F\u60F3\u8BFB\u53D6\u6570\u636E\uFF0C\u800C\u5374\u975E\u5F97\u8981\u5148\u53D1\u8D77\u4E00\u4E2Aselect\u8BE2\u95EE\u6570\u636E\u72B6\u6001\u7684\u8BF7\u6C42\uFF0C\u7136\u540E\u518D\u53D1\u8D77\u771F\u6B63\u7684\u8BFB\u53D6\u6570\u636E\u8BF7\u6C42,\u80FD\u4E0D\u80FD\u6709\u4E00\u79CD\u4E00\u52B3\u6C38\u9038\u7684\u65B9\u5F0F\uFF0C\u6211\u53EA\u8981\u53D1\u9001\u4E00\u4E2A\u8BF7\u6C42\u6211\u544A\u8BC9\u5185\u6838\u6211\u8981\u8BFB\u53D6\u6570\u636E\uFF0C\u7136\u540E\u6211\u5C31\u4EC0\u4E48\u90FD\u4E0D\u7BA1\u4E86\uFF0C\u7136\u540E\u5185\u6838\u53BB\u5E2E\u6211\u53BB\u5B8C\u6210\u5269\u4E0B\u7684\u6240\u6709\u4E8B\u60C5\uFF1F</p><p>\u5F53\u7136\u65E2\u7136\u4F60\u60F3\u5F97\u51FA\u6765\uFF0C\u90A3\u4E48\u5C31\u4F1A\u6709\u4EBA\u505A\u5F97\u5230\uFF0C\u6709\u4EBA\u8BBE\u8BA1\u4E86\u4E00\u79CD\u65B9\u6848\uFF0C\u5E94\u7528\u53EA\u9700\u8981\u5411\u5185\u6838\u53D1\u9001\u4E00\u4E2Aread \u8BF7\u6C42,\u544A\u8BC9\u5185\u6838\u5B83\u8981\u8BFB\u53D6\u6570\u636E\u540E\u5373\u523B\u8FD4\u56DE\uFF1B\u5185\u6838\u6536\u5230\u8BF7\u6C42\u540E\u4F1A\u5EFA\u7ACB\u4E00\u4E2A\u4FE1\u53F7\u8054\u7CFB\uFF0C\u5F53\u6570\u636E\u51C6\u5907\u5C31\u7EEA\uFF0C\u5185\u6838\u4F1A\u4E3B\u52A8\u628A\u6570\u636E\u4ECE\u5185\u6838\u590D\u5236\u5230\u7528\u6237\u7A7A\u95F4\uFF0C\u7B49\u6240\u6709\u64CD\u4F5C\u90FD\u5B8C\u6210\u4E4B\u540E\uFF0C\u5185\u6838\u4F1A\u53D1\u8D77\u4E00\u4E2A\u901A\u77E5\u544A\u8BC9\u5E94\u7528\uFF0C\u6211\u4EEC\u79F0\u8FD9\u79CD\u4E00\u52B3\u6C38\u9038\u7684\u6A21\u5F0F\u4E3A\u5F02\u6B65IO\u6A21\u578B\u3002 <img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100942.png" alt="20221227100942" loading="lazy"> \u672F\u8BED\u63CF\u8FF0\uFF1A \u5E94\u7528\u544A\u77E5\u5185\u6838\u542F\u52A8\u67D0\u4E2A\u64CD\u4F5C\uFF0C\u5E76\u8BA9\u5185\u6838\u5728\u6574\u4E2A\u64CD\u4F5C\u5B8C\u6210\u4E4B\u540E\uFF0C\u901A\u77E5\u5E94\u7528\uFF0C\u8FD9\u79CD\u6A21\u578B\u4E0E\u4FE1\u53F7\u9A71\u52A8\u6A21\u578B\u7684\u4E3B\u8981\u533A\u522B\u5728\u4E8E\uFF0C\u4FE1\u53F7\u9A71\u52A8IO\u53EA\u662F\u7531\u5185\u6838\u901A\u77E5\u6211\u4EEC\u5408\u9002\u53EF\u4EE5\u5F00\u59CB\u4E0B\u4E00\u4E2AIO\u64CD\u4F5C\uFF0C\u800C\u5F02\u6B65IO\u6A21\u578B\u662F\u7531\u5185\u6838\u901A\u77E5\u6211\u4EEC\u64CD\u4F5C\u4EC0\u4E48\u65F6\u5019\u5B8C\u6210\u3002 <img src="https://afatpig.oss-cn-chengdu.aliyuncs.com/blog/20221227100952.png" alt="20221227100952" loading="lazy"></p><p>\u603B\u7ED3\uFF1A\u5F02\u6B65IO\u7684\u4F18\u5316\u601D\u8DEF\u662F\u89E3\u51B3\u4E86\u5E94\u7528\u7A0B\u5E8F\u9700\u8981\u5148\u540E\u53D1\u9001\u8BE2\u95EE\u8BF7\u6C42\u3001\u53D1\u9001\u63A5\u6536\u6570\u636E\u8BF7\u6C42\u4E24\u4E2A\u9636\u6BB5\u7684\u6A21\u5F0F\uFF0C\u5728\u5F02\u6B65IO\u7684\u6A21\u5F0F\u4E0B\uFF0C\u53EA\u9700\u8981\u5411\u5185\u6838\u53D1\u9001\u4E00\u6B21\u8BF7\u6C42\u5C31\u53EF\u4EE5\u5B8C\u6210\u72B6\u6001\u8BE2\u95EE\u548C\u6570\u62F7\u8D1D\u7684\u6240\u6709\u64CD\u4F5C\u3002</p><h2 id="_6\u3001\u518D\u8C08io\u6A21\u578B\u91CC\u9762\u7684\u540C\u6B65\u5F02\u6B65" tabindex="-1"><a class="header-anchor" href="#_6\u3001\u518D\u8C08io\u6A21\u578B\u91CC\u9762\u7684\u540C\u6B65\u5F02\u6B65" aria-hidden="true">#</a> 6\u3001\u518D\u8C08IO\u6A21\u578B\u91CC\u9762\u7684\u540C\u6B65\u5F02\u6B65</h2><p>\u6211\u4EEC\u901A\u5E38\u4F1A\u8BF4\u5230\u540C\u6B65\u963B\u585EIO\u3001\u540C\u6B65\u975E\u963B\u585EIO\uFF0C\u5F02\u6B65IO\u51E0\u79CD\u672F\u8BED\uFF0C\u901A\u8FC7\u4E0A\u9762\u7684\u5185\u5BB9\uFF0C\u90A3\u4E48\u6211\u60F3\u4F60\u73B0\u5728\u80AF\u5B9A\u5DF2\u7ECF\u7406\u89E3\u4E86\u4EC0\u4E48\u662F\u963B\u585E\u4EC0\u4E48\u662F\u975E\u963B\u585E\u4E86\uFF0C\u6240\u8C13\u963B\u585E\u5C31\u662F\u53D1\u8D77\u8BFB\u53D6\u6570\u636E\u8BF7\u6C42\u7684\u65F6\uFF0C\u5F53\u6570\u636E\u8FD8\u6CA1\u51C6\u5907\u5C31\u7EEA\u7684\u65F6\u5019\uFF0C\u8FD9\u65F6\u8BF7\u6C42\u662F\u5373\u523B\u8FD4\u56DE\uFF0C\u8FD8\u662F\u5728\u8FD9\u91CC\u7B49\u5F85\u6570\u636E\u7684\u5C31\u7EEA\uFF0C\u5982\u679C\u9700\u8981\u7B49\u5F85\u7684\u8BDD\u5C31\u662F\u963B\u585E\uFF0C\u53CD\u4E4B\u5982\u679C\u5373\u523B\u8FD4\u56DE\u5C31\u662F\u975E\u963B\u585E\u3002</p><p>\u6211\u4EEC\u533A\u5206\u4E86\u963B\u585E\u548C\u975E\u963B\u585E\u540E\u518D\u6765\u5206\u522B\u4E0B\u540C\u6B65\u548C\u5F02\u6B65\uFF0C\u5728IO\u6A21\u578B\u91CC\u9762\u5982\u679C\u8BF7\u6C42\u65B9\u4ECE\u53D1\u8D77\u8BF7\u6C42\u5230\u6570\u636E\u6700\u540E\u5B8C\u6210\u7684\u8FD9\u4E00\u6BB5\u8FC7\u7A0B\u4E2D\u90FD\u9700\u8981\u81EA\u5DF1\u53C2\u4E0E\uFF0C\u90A3\u4E48\u8FD9\u79CD\u6211\u4EEC\u5C31\u79F0\u4E3A\u540C\u6B65\u8BF7\u6C42\uFF1B\u53CD\u4E4B\uFF0C\u5982\u679C\u5E94\u7528\u53D1\u9001\u5B8C\u6307\u4EE4\u540E\u5C31\u4E0D\u518D\u53C2\u4E0E\u8FC7\u7A0B\u4E86\uFF0C\u53EA\u9700\u8981\u7B49\u5F85\u6700\u7EC8\u5B8C\u6210\u7ED3\u679C\u7684\u901A\u77E5\uFF0C\u90A3\u4E48\u8FD9\u5C31\u5C5E\u4E8E\u5F02\u6B65\u3002</p><p>\u6211\u4EEC\u518D\u770B\u540C\u6B65\u963B\u585E\u3001\u540C\u6B65\u975E\u963B\u585E\uFF0C\u4ED6\u4EEC\u4E0D\u540C\u7684\u53EA\u662F\u53D1\u8D77\u8BFB\u53D6\u8BF7\u6C42\u7684\u65F6\u5019\u4E00\u4E2A\u8BF7\u6C42\u963B\u585E\uFF0C\u4E00\u4E2A\u8BF7\u6C42\u4E0D\u963B\u585E\uFF0C\u4F46\u662F\u76F8\u540C\u7684\u662F\uFF0C\u4ED6\u4EEC\u90FD\u9700\u8981\u5E94\u7528\u81EA\u5DF1\u76D1\u63A7\u6574\u4E2A\u6570\u636E\u5B8C\u6210\u7684\u8FC7\u7A0B\u3002\u800C\u4E3A\u4EC0\u4E48\u53EA\u6709\u5F02\u6B65\u975E\u963B\u585E \u800C\u6CA1\u6709\u5F02\u6B65\u963B\u585E\u5462\uFF0C\u56E0\u4E3A\u5F02\u6B65\u6A21\u578B\u4E0B\u8BF7\u6C42\u6307\u5B9A\u53D1\u9001\u5B8C\u540E\u5C31\u5373\u523B\u8FD4\u56DE\u4E86\uFF0C\u6CA1\u6709\u4EFB\u4F55\u540E\u7EED\u6D41\u7A0B\u4E86\uFF0C\u6240\u4EE5\u5B83\u6CE8\u5B9A\u4E0D\u4F1A\u963B\u585E\uFF0C\u6240\u4EE5\u4E5F\u5C31\u53EA\u4F1A\u6709\u5F02\u6B65\u975E\u963B\u585E\u6A21\u578B\u4E86\u3002</p>',64);function m(u,B){const a=s("ExternalLinkIcon");return t(),n("div",null,[p("p",null,[l,p("a",d,[h,o(a)]),g,p("a",f,[I,o(a)]),O]),_])}var C=c(i,[["render",m],["__file","IO-Model.html.vue"]]);export{C as default};
